import React from 'react';
import {
     VictoryChart,
     VictoryAxis, 
     VictoryBar, 
     VictoryTheme, 
     VictoryVoronoiContainer,
     VictoryLabel,
        VictoryLine
    } from 'victory';

const sharedAxisStyles = {
    tickLabels: {
      fontSize: 10
    },
    axisLabel: {
      padding: 45,
      fontSize: 10,
      fontStyle: "italic"
    }
  };

function configData(json) {
    console.log(json);
    const data = [];
    let endIdx = json.length-1;
    for (let i = endIdx; i >= 1; i--){
        let idx = endIdx-i;
        let posIncr = (i===0 ? 0 : (json[idx].positive - json[idx+1].positive));
        data.push({
            index: i,
            date: json[idx].date,
            positive: json[idx].positive, 
            posIncr: posIncr,
            recovered: json[idx].recovered

        });
    }
    console.log('data' + JSON.stringify(data));
    return data;
}

function date(index, json){
    let date = json[index].date.toString();
    date = date.substr(4,2) + "/" + date.substr(6) + "/" + date.substr(0,4);
    return date;
}
export default class FetchData extends React.Component {
    state = {
        loading: true,
        json: {}
        //NOTE: json objects ordered from most recent date to oldest date
    };

    async componentDidMount() {
        const url = "https://covidtracking.com/api/us/daily";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({loading: false, json: data});
    }


    render() {
        if (this.state.loading){
            return(<div>loading...</div>);
        }

        return (
           <> 
           {/* Bar Chart: Change in Positive Cases of COVID-19 USA */}
            <VictoryChart
            height={400}
            width={400}
            padding={100}
            theme={VictoryTheme.material}
            animate={{
                duration: 500
            }}
            containerComponent={
                <VictoryVoronoiContainer
                  labels={({ datum }) => `${date(datum.index, this.state.json)}: ${datum.posIncr} new case(s)`}
                />
              }
            >
                 <VictoryLabel
                    fontSize={10}
                    x={200}
                    y={60}
                    textAnchor="middle"
                    text="Change in Positive Cases of COVID-19 USA"
                />
                <VictoryAxis
                    style={sharedAxisStyles}
                    tickValues={[0, this.state.json.length]}
                    tickFormat={[date(this.state.json.length-1, this.state.json), date(0, this.state.json)]}
                ></VictoryAxis>
                <VictoryAxis
                    dependentAxis
                    tickFormat={(x) => (`${x / 1000}k`)}
                    label="Number of New Cases"
                    style={sharedAxisStyles}
                ></VictoryAxis>

                <VictoryBar
                    data={configData(this.state.json)}
                    x="index"
                    y="posIncr"          
                />
            </VictoryChart>

            <VictoryChart
                height={400}
                width={400}
                padding={100}
                animate={{
                    duration: 500
                }}
            >

            </VictoryChart>
                <VictoryLine
                interpolation="natural"
                // data={sampleData}
                />
             </>
        );
    }
}
