import React from 'react';

export default class ManageData extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            apiLink: '',
            apiJson: ''
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }



    handleChange(event) {
        this.setState({apiLink: event.target.value});
    }

    // pull data from API at [url], set to json, update state apiJson
    apiToJson (url) {
        fetch(url).then(function(response){
            return response.json();
        }).then(function(data){
            console.log(data);
            this.setState({loading: false, apiJson: data});
        }).catch(function(err){
            console.log('unable to fetch data from API: ' + err);
        });
    }

    handleSubmit(event) {
        link = this.state.apiLink;
        alert('An API link was submitted: ' + link);


        fetch('some-node-endpoint', {
            method: 'POST',
            body: JSON.stringify({
              task: self.refs.task.value
            }),
            headers: {"Content-Type": "application/json"}
          })
          .then(function(response){
            return response.json()
          }).then(function(body){
            console.log(body);
            alert(self.refs.task.value)
          });
        event.preventDefault();
    }

    render(){
        return (
            <form style={{margin: 30}} onSubmit={this.handleSubmit}>
                <label>
                    API Link: &nbsp;
                    <input type="text" name="apiLink" value={this.state.value} onChange={this.handleChange}/>
                </label>
                <input type="submit" value="Initialize Database" />
            </form>
        );
    }
}
