import React from 'react';
import {
     VictoryChart,
     VictoryAxis, 
     VictoryBar, 
     VictoryTheme, 
     VictoryVoronoiContainer,
     VictoryLabel,
     VictoryStack,
     VictoryArea,
     VictoryScatter
    } from 'victory';


function configData(json) {
    // const data = [];  
    // for (let i = 0; i < json.length; i++){
    //     let obj = json[i];
    //     data.push({
    //         country: obj.Country,
    //         totConfirmed: obj.TotalConfirmed,
    //         totDeaths: obj.TotalDeaths, 
    //         totRecovered: obj.TotalRecovered,
    //     });
    // }
    return json.Countries;
}

    function bubbleChartData(json, metric) {
        let array = configData(json);
        let bubbleData = array.map(obj => ({x: obj.totDeaths, y: obj[metric], amount: obj.totConfirmed}));
        console.log(bubbleData);
        return bubbleData;
    }

export default class BubbleChart extends React.Component {
    state = {
        loading: true,
        json: {}

    };

    async componentDidMount() {
        const url = "https://api.covid19api.com/summary";
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);
        this.setState({loading: false, json: data});
    }

    render() {
        if (this.state.loading){
            return(<div>loading...</div>);
        }

        return (

            <VictoryChart
            height={400}
            width={400}
            padding={100}
            theme={
                VictoryTheme.material
            }
            animate={{
                duration: 500
            }}
            >
            <VictoryScatter
             style={{ data: { fill: "#c43a31" } }}
             bubbleProperty="amount"
             maxBubbleSize={25}
             minBubbleSize={5}
             data={bubbleChartData(this.state.json, 'totRecovered')}
             >

            </VictoryScatter>
            </VictoryChart>

            ); 
    }
}
