import React from 'react';
import {
     VictoryChart,
     VictoryAxis, 
     VictoryBar, 
     VictoryTheme, 
     VictoryVoronoiContainer,
     VictoryLabel,
     VictoryStack,
     VictoryArea,
     VictoryScatter,
     VictoryZoomContainer,
     createContainer,
     VictoryLegend,
     VictoryLine
    } from 'victory';

const sharedAxisStyles = {
    tickLabels: {
        fontSize: 10
    },
    axisLabel: {
        padding: 45,
        fontSize: 10,
        fontStyle: "italic"
    }
    };

const VictoryZoomVoronoiContainer = createContainer("zoom", "voronoi");

function bubbleChartData(json, metric) {
    let array = json.Countries;
    let bubbleData = array.map(obj => ({
        x: obj.TotalDeaths, 
        y: obj[metric], 
        amount: obj.TotalConfirmed, 
        country: obj.Country,
        countryCode: obj.CountryCode
    }));
    return bubbleData;
}


export default class BubbleChart extends React.Component {
    state = {
        loading: true,
        json: {},
        countryCode : ''
    };

    async componentDidMount() {
        const url = "https://api.covid19api.com/summary";
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);
        this.setState({loading: false, json: data});
    }

    render() {
        if (this.state.loading){
            return(<div>loading...</div>);
        }

        if (this.state.countryCode !== ''){
            return(
            <div><CountryChart countryCode={this.state.countryCode}/></div>)
        }

        return (
        <div style={{ display: "flex", flexWrap: "wrap", paddingLeft: '15%', marginTop: -50 }}>
            <VictoryChart
            style={{ parent: { maxWidth: "80%" } }}
            height={400}
            width={500}
            padding={100}
            theme={
                VictoryTheme.material
            }
            animate={{
                duration: 10000
            }}
            domain={{x: [0, 130000], y: [0, 700000]}}
            containerComponent={
                <VictoryZoomVoronoiContainer 
                zoomDomain={{x: [0, 130000], y: [0, 700000]}}
                labels={({ datum }) => `${datum.country}: ${datum.amount} confirmed case(s)`}
                />
              }
            >   
            <VictoryLabel
                    fontSize={10}
                    x={250}
                    y={60}
                    textAnchor="middle"
                    text="World: Total Deaths vs Recoveries per Country"
                />
            <VictoryAxis
                style={sharedAxisStyles}
                tickFormat={(x) => (`${x / 1000}k`)}
                label="Total Recoveries"
            ></VictoryAxis>
            <VictoryAxis
                dependentAxis
                tickFormat={(x) => (`${x / 1000}k`)}
                label="Total Deaths"
                style={sharedAxisStyles}
            ></VictoryAxis>
            <VictoryScatter
             style={{ data: { fill: "#c43a31", opacity: "50%" } }}
             bubbleProperty="amount"
             maxBubbleSize={20}
             minBubbleSize={1}
             data={bubbleChartData(this.state.json, 'TotalRecovered')}
             events={[{
                 target: 'data',
                 eventHandlers: {
                     onClick: (event, data) => {
                         let cCode = data.datum.countryCode
                         console.log(cCode);
                         this.setState({countryCode: cCode});
                     },
                 },
             }]}
             >

            </VictoryScatter>
            </VictoryChart>
        </div>
            ); 
    }
}

function configData(json) {
    const data = [];
    let endIdx = json.length-1;
    for (let i = endIdx; i >= 0; i--){
        let idx = endIdx-i;
        let obj = json[idx];
        let posIncr = (i===0 ? 0 : (obj.Confirmed - json[idx+1].Confirmed));
        let recovIncr = (i===0 ? 0 : (obj.Recovered - json[idx+1].Recovered));
        let deathIncr = (i===0 ? 0 : (obj.Deaths - json[idx+1].Deaths));
        let state = (obj.state ? '' : obj.state);
        data.push({
            index: i,
            date: obj.date,
            positive: obj.positive, 
            posIncr: posIncr,
            recovered: obj.recovered,
            recovIncr: recovIncr,
            death: obj.death,
            deathIncr: deathIncr,
            state: state
        });
    }
    return data;
}

function lineChartData(json, metric) {
    let array = configData(json);
    let lineData = array.map(obj => ({x: obj.index, y: obj[metric]}));
    return lineData;
}

function date(index, json){
    let date = json[json.length-1-index].date;
    return date;
}

class CountryChart extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            loading: true,
            json: {},
        };
    }

    async componentDidMount() {
        let cc = this.props.countryCode.toLowerCase();
        const url = "https://api.covid19api.com/live/country/"+ cc +"/status/confirmed/date/2020-03-21T13:13:30Z";
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);
        this.setState({loading: false, json: data});
    }

    render() {
        if (this.state.loading){
            return(<div>loading...</div>);
        }

        return (
       <>
            <div>{this.props.countryCode}</div>

        <VictoryChart
            height={400}
            width={400}

            padding={100}
            theme={VictoryTheme.material}
            animate={{
                duration: 1000
            }}
            style={{ parent: { maxWidth: "30%" } }}
        >
        <VictoryLegend 
            orientation="vertical"
            y={100}
            x={295}
            symbolSpacer={10}
            gutter={10}
            style={{  title: {fontSize: 10 } }}
            data={[
            { name: "Positive Cases", symbol: { fill: "red", type: "star" } },
            { name: "Recoveries", symbol: { fill: "blue", type: "square" } },
            { name: "Deaths", symbol: { fill: "black", type: "square" } }
            ]}
        />
         <VictoryLabel
            fontSize={5}
            x={200}
            y={60}
            textAnchor="middle"
            text= {this.props.countryCode + ": Daily Change in Positive Cases, Recoveries, Deaths"}
        />
        <VictoryAxis
            style={sharedAxisStyles}
            tickValues={[0, this.state.json.length]}
            // tickFormat={[date(0, this.state.json), date(this.state.json.length-1, this.state.json)]}
        ></VictoryAxis>
        <VictoryAxis
            dependentAxis
            tickFormat={(x) => (`${x / 1000}k`)}
            label="Number of People"
            style={sharedAxisStyles}
        ></VictoryAxis>
        <VictoryLine
        interpolation="natural"
        style={{ data: { stroke: "#c43a31" } }}
        data={lineChartData(this.state.json, 'posIncr')}
        />

        <VictoryStack>

            <VictoryArea
            interpolation="natural"
            style={{ data: { fill: "black" } }}
            data={lineChartData(this.state.json, 'deathIncr')}
            />
            <VictoryArea
            interpolation="natural"
            style={{ data: { fill: "blue" } }}
            data={lineChartData(this.state.json, 'recovIncr')}
            />
        </VictoryStack>
        </VictoryChart>
 </>

        );
    }
}
