import React from 'react';
import {
     VictoryChart,
     VictoryAxis, 
     VictoryBar, 
     VictoryTheme, 
     VictoryVoronoiContainer,
     VictoryLabel,
     VictoryStack,
     VictoryArea,
     VictoryScatter,
     VictoryZoomContainer,
     createContainer,
     VictoryLegend,
     VictoryLine
    } from 'victory';

const sharedAxisStyles = {
    tickLabels: {
        fontSize: 10
    },
    axisLabel: {
        padding: 45,
        fontSize: 10,
        fontStyle: "italic"
    }
    };

function configData(json) {
    const data = [];
    let endIdx = json.length-1;
    for (let i = endIdx; i >= 0; i--){
        let idx = endIdx-i;
        let obj = json[idx];
        // let changeConf = (i===0 ? 0 : (json[idx+1].Cases) - obj.Cases);
        // let recovIncr = (i===0 ? 0 : (json[idx+1].Recovered - obj.Recovered));
        // let deathIncr = (i===0 ? 0 : (json[idx+1].Deaths - obj.Deaths));
        // let state = (obj.state ? '' : obj.state);
        data.push({
            index: i,
            date: obj.Date,
            confirmed: obj.Cases,
            // changeConfirmed: changeConf
            // positive: obj.positive, 
            // posIncr: posIncr,
            // recovered: obj.recovered,
            // recovIncr: recovIncr,
            // death: obj.death,
            // deathIncr: deathIncr,
            // state: state
        });
    }
    return data;
}

function lineChartData(json, metric) {
    let array = configData(json);
    let lineData = array.map(obj => ({x: obj.index, y: obj[metric]}));
    return lineData;
}

function date(index, json){
    let date = json[index].Date.split("T")[0];
    return date;
}

export default class CountryChart extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            loading: true,
            //dates ordered from least recent to most recent
            json: {},
        };
    }

    async componentDidMount() {
        let cc = this.props.countryCode.toLowerCase();
        const url = "https://api.covid19api.com/total/country/"+cc+"/status/confirmed";
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);
        this.setState({loading: false, json: data});
    }

    render() {
        if (this.state.loading){
            return(<div>loading...</div>);
        }

        return (
       <>
            <div>{this.props.countryCode}</div>

        <VictoryChart
            height={400}
            width={400}

            padding={100}
            theme={VictoryTheme.material}
            animate={{
                duration: 1000
            }}
            style={{ parent: { maxWidth: "30%" } }}
            containerComponent={
                <VictoryVoronoiContainer
                  labels={({ datum }) => 
                    `${date(datum.index, this.state.json)}: ${datum.confirmed} new case(s)`


                }
                />
              }
        >
        <VictoryLegend 
            orientation="vertical"
            y={100}
            x={295}
            symbolSpacer={10}
            gutter={10}
            style={{  title: {fontSize: 10 } }}
            data={[
            { name: "Positive Cases", symbol: { fill: "red", type: "star" } },
            { name: "Recoveries", symbol: { fill: "blue", type: "square" } },
            { name: "Deaths", symbol: { fill: "black", type: "square" } }
            ]}
        />
         <VictoryLabel
            fontSize={5}
            x={200}
            y={60}
            textAnchor="middle"
            text= {this.props.countryCode + ": Daily Change in Positive Cases, Recoveries, Deaths"}
        />
        <VictoryAxis
            style={sharedAxisStyles}
            tickValues={[0, this.state.json.length]}
            tickFormat={[date(0, this.state.json), date(this.state.json.length-1, this.state.json)]}
        ></VictoryAxis>
        <VictoryAxis
            dependentAxis
            tickFormat={(x) => (`${x / 1000}k`)}
            label="Number of People"
            style={sharedAxisStyles}
        ></VictoryAxis>
        <VictoryLine
        interpolation="natural"
        style={{ data: { stroke: "#c43a31" } }}
        data={configData(this.state.json, 'confirmed')}
        x="index"
        y="confirmed"  
        />
{/* 
        <VictoryStack>

            <VictoryArea
            interpolation="natural"
            style={{ data: { fill: "black" } }}
            data={lineChartData(this.state.json, 'deathIncr')}
            />
            <VictoryArea
            interpolation="natural"
            style={{ data: { fill: "blue" } }}
            data={lineChartData(this.state.json, 'recovIncr')}
            />
        </VictoryStack> */}
        </VictoryChart>
 </>

        );
    }
}
