import React from 'react';
import {
    VictoryPie,
    VictoryBar,
    VictoryChart,
    VictoryPolarAxis,
    VictoryStack,
    VictoryTheme,
    VictoryGroup,
    VictoryLabel,
    VictoryTooltip,
   } from 'victory';
export default class DataTwo extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            loading: true,
            json: {},
        }
    }
    async callDB() {
        await fetch("http://localhost:9000/worldDB")
            .then(res => {
                return res.json();
            }).then(json => {
                this.setState({json: Array.from(json)});
            }).catch(err => err);
    }

    async componentDidMount() {
        await this.callDB();
        this.setState({loading: false});
        console.log(this.state.json);
        console.log(this.state.json[0]["Annual Totals"][9].Period);
        console.log(this.state.json[0]["Annual Totals"].splice(1,1));
    }

    render(){
        if (this.state.loading || this.state.json.length === 0){
            return(<div>loading...</div>);
        } else {
            return (
                <div className="Data2">
                    <h1 style={{marginTop: 50}}>Energy in the USA</h1>
                    <DonutChart data={this.state.json[0]["Annual Totals"]}/>
                    <PieChart data={this.state.json[0]["Annual Totals"]}/>
                </div>
            );
        }
    }
}

let sampleDataPolar = [
    { x: 100, y: 3},
];

let sampleDataPolar2 = [
    { x: 300, y: 10},
    { x: 300, y: 5},
];

let sampleDataPolar3 = [
    { x: 10, y: 4},
];



class CustomLabel extends React.Component {
    render() {
        return (
        <g>
            {/* <VictoryLabel {...this.props}/> */}
            <VictoryTooltip
            {...this.props}
            x={200} y={275}
            orientation="top"
            pointerLength={0}
            cornerRadius={75}
            flyoutWidth={150}
            flyoutHeight={180}
            flyoutStyle={{ fill: "none", stroke:"none"}}
            style={{ labels: { fill: "black", fontSize:15} }}
            />
        </g>
        );
    }
}

CustomLabel.defaultEvents = VictoryTooltip.defaultEvents;

class DonutChart extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            sum: 0
        }
    }
    calculateSum() {
        let obj2019 = this.props.data[9]
        let dataArray = Object.entries(obj2019).splice(2);
        console.log(dataArray);
        let sum = 0;
        for (let i = 0; i < dataArray.length; i++){
            let value = dataArray[i][1];
            let parsedVal = parseFloat(value.replace(/,/g, ''));
            sum += parsedVal;
        }
        this.setState({sum: sum});
    }

    configData () {
        let obj2019 = this.props.data[9]
        console.log(obj2019);
        let year = obj2019.Period;
        // tuple array with each entry containing the key and value
        let dataArray = Object.entries(obj2019).splice(2);
        console.log(dataArray);
        let pieData = [];
        if (this.state.sum !== 0){
            pieData = dataArray.map(obj => 
                ({
                    x: obj[0],
                    y: parseFloat(obj[1].replace(/,/g, '')),
                    percent: (parseFloat(obj[1].replace(/,/g, ''))/this.state.sum*100).toFixed(2),
                }
                ));
        }
        return pieData;
    }

    componentDidMount(){
        this.calculateSum();
        this.configData();
    }

    render() {
        return (
            <>
            <div>Net Generation from Renewable Sources</div>
            <div>(Thousand Megawatthours)</div>
            <div style={{marginLeft: "30%", marginRight: "30%"}}>
                <VictoryPie
                    colorScale={["#8DC3A7", "#6BAF92", "#4E9C81", "#358873", "#207567" ]}
                    outerRadius={250}
                    innerRadius={100}
                    labelRadius={160}
                    // labels={({ datum }) => `${datum.percent}% \n -------------- \n${datum.x}`}
                    labels={({ datum }) => `${datum.percent}% ${datum.x}`}
                    style={{ labels: { fill: "black", fontSize: 10} }}
                    labelComponent={<CustomLabel />}
                    data={this.configData()}
                />
            </div>
            </>
        )
    }
}

class PieChart extends React.Component {
    constructor(props){
        super(props);
    }

    render(){
        return (
            <>
            <div style={{marginLeft: "30%", marginRight: "30%"}}>
                <VictoryPie
                // style={{ parent: { minWidth: "50%" }}}
                colorScale={["tomato", "orange", "gold", "cyan", "navy", "white" ]}
                data={[
                    { x: "Cats", y: 35 },
                    { x: "Dogs", y: 40 },
                    { x: "Birds", y: 55 },
                    { x: "Frogs", y: 55 },
                    { x: "Mice", y: 55 },
                    { x: "Snakes", y: 10 }
                ]}
                // data={this.configData()}
                />
            </div>
            <div style={{marginLeft: "30%", marginRight: "30%"}}>
                <VictoryChart
                theme={VictoryTheme.material}
                domain={{ y: [0.5, 5.5] }}
                >
                    <VictoryGroup horizontal
                    offset={10}
                    style={{ data: { width: 6 } }}
                    colorScale={["brown", "tomato", "gold"]}
                    >
                    <VictoryBar
                        data={[
                        { x: 1, y: 1 },
                        { x: 2, y: 2 },
                        { x: 3, y: 3 },
                        { x: 4, y: 2 },
                        { x: 5, y: 1 }
                        ]}
                    />
                    <VictoryBar
                        data={[
                        { x: 1, y: 2 },
                        { x: 2, y: 3 },
                        { x: 3, y: 4 },
                        { x: 4, y: 5 },
                        { x: 5, y: 5 }
                        ]}
                    />
                    <VictoryBar
                        data={[
                        { x: 1, y: 1 },
                        { x: 2, y: 2 },
                        { x: 3, y: 3 },
                        { x: 4, y: 4 },
                        { x: 5, y: 4 }
                        ]}
                    />
                </VictoryGroup>
                </VictoryChart>
            </div>



            <div style={{marginLeft: "30%", marginRight: "30%"}}>
            <VictoryBar polar

                data={[
                    { x: 100, y: 3 },
                    { x: 6, y: 5 },
                    { x: 50, y: 2 },

                ]}
                width={400} height={400}
                domain={{ x: [0, 360], y: [0, 5] }}
                style={{ data: { fill: "#c43a31", stroke: "black", strokeWidth: 2 } }}
            />
            </div>

            <div style={{marginLeft: "30%", marginRight: "30%"}}>
                <VictoryChart polar
                    maxDomain={{ x: 360 }}
                    height={250} width={250}
                    padding={30}
                >
                <VictoryPolarAxis dependentAxis
                    style={{
                    axis: {stroke: "none"},
                    tickLabels: { fill: "none"},
                    grid: { stroke: "grey", strokeDasharray: "4, 8" }
                    }}
                />
                <VictoryPolarAxis
                    tickValues={[0, 45, 90, 135, 180, 225, 270, 315]}
                />
                <VictoryStack
                    colorScale={["#ad1b11", "#c43a31", "#dc7a6b", "pink", "green", "blue"]}
                    style={{ data: { width: 50} }}
                >
                    <VictoryBar data={sampleDataPolar}/>
                    <VictoryBar data={sampleDataPolar2}/>
                    <VictoryBar data={sampleDataPolar3}/>
                </VictoryStack>
                </VictoryChart>
            </div>



            </>
        );
    }
}
