import React from 'react';
import {
     VictoryChart,
     VictoryAxis, 
     VictoryBar, 
     VictoryTheme, 
     VictoryVoronoiContainer,
     VictoryLabel,
     VictoryStack,
     VictoryArea,
     VictoryScatter
    } from 'victory';

const sharedAxisStyles = {
    tickLabels: {
        fontSize: 10
    },
    axisLabel: {
        padding: 45,
        fontSize: 10,
        fontStyle: "italic"
    }
    };

function configData(json) {
    return json.Countries;
}

function bubbleChartData(json, metric) {
    let array = configData(json);
    let bubbleData = array.map(obj => ({x: obj.TotalDeaths, y: obj[metric], amount: obj.TotalConfirmed}));
    console.log(bubbleData);
    return bubbleData;
}

export default class BubbleChart extends React.Component {
    state = {
        loading: true,
        json: {}

    };

    async componentDidMount() {
        const url = "https://api.covid19api.com/summary";
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);
        this.setState({loading: false, json: data});
    }

    render() {
        if (this.state.loading){
            return(<div>loading...</div>);
        }

        return (

            <VictoryChart
            height={400}
            width={400}
            padding={100}
            theme={
                VictoryTheme.material
            }
            animate={{
                duration: 10000
            }}
            containerComponent={
                <VictoryVoronoiContainer
                  labels={({ datum }) => `${datum.Country}: ${datum.TotalConfirmed} confirmed case(s)`}
                />
              }
            >
            <VictoryAxis
                style={sharedAxisStyles}
                tickFormat={(x) => (`${x / 1000}k`)}
            ></VictoryAxis>
            <VictoryAxis
                dependentAxis
                tickFormat={(x) => (`${x / 1000}k`)}
                label="Number of People"
                style={sharedAxisStyles}
            ></VictoryAxis>
            <VictoryScatter
             style={{ data: { fill: "#c43a31" } }}
             bubbleProperty="amount"
             maxBubbleSize={25}
             minBubbleSize={1}
             data={bubbleChartData(this.state.json, 'TotalRecovered')}
             >

            </VictoryScatter>
            </VictoryChart>

            ); 
    }
}
