import React from 'react';
import {
    VictoryPie,
    VictoryBar,
    VictoryChart,
    VictoryPolarAxis,
    VictoryStack,
    VictoryTheme,
    VictoryGroup,
    VictoryLabel,
    VictoryTooltip,
   } from 'victory';
export default class DataTwo extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            loading: true,
            json: {},
        }
    }
    async callDB() {
        await fetch("http://localhost:9000/worldDB")
            .then(res => {
                return res.json();
            }).then(json => {
                this.setState({json: Array.from(json)});
            }).catch(err => err);
    }

    async componentDidMount() {
        await this.callDB();
        this.setState({loading: false});
        console.log(this.state.json);
        console.log(this.state.json[0]["Annual Totals"][0]);
        console.log(this.state.json[0]["Annual Totals"].splice(1,1));
    }

    render(){
        if (this.state.loading || this.state.json.length === 0){
            return(<div>loading...</div>);
        } else {
            return (
                <div className="Data2">
                    <h1 style={{marginTop: 50}}>???</h1>
                    <PieChart data={this.state.json[0]["Annual Totals"]}/>
                </div>
            );
        }
    }
}

let sampleDataPolar = [
    { x: 100, y: 3},
];

let sampleDataPolar2 = [
    { x: 300, y: 10},
    { x: 300, y: 5},
];

let sampleDataPolar3 = [
    { x: 10, y: 4},
];



class CustomLabel extends React.Component {
    render() {
        return (
        <g>
            <VictoryLabel {...this.props}/>
            <VictoryTooltip
            {...this.props}
            x={200} y={250}
            orientation="top"
            pointerLength={0}
            cornerRadius={50}
            flyoutWidth={100}
            flyoutHeight={100}
            flyoutStyle={{ fill: "black" }}
            />
        </g>
        );
    }
}

CustomLabel.defaultEvents = VictoryTooltip.defaultEvents;

class PieChart extends React.Component {
    configData () {
        let year = this.props.json.Period;
        let json = this.props.json.splice(1,1);
        let array = Array.from(this.props.json);
        let pieData = array.map(obj => {

        })
    }
    render(){
        return (
            <>
            <div style={{marginLeft: "30%", marginRight: "30%"}}>
                <VictoryPie
                // style={{ parent: { minWidth: "50%" }}}
                colorScale={["tomato", "orange", "gold", "cyan", "navy", "white" ]}
                data={[
                    { x: "Cats", y: 35 },
                    { x: "Dogs", y: 40 },
                    { x: "Birds", y: 55 },
                    { x: "Frogs", y: 55 },
                    { x: "Mice", y: 55 },
                    { x: "Snakes", y: 10 }
                ]}
                />
            </div>
            <div style={{marginLeft: "30%", marginRight: "30%"}}>
                <VictoryChart
                theme={VictoryTheme.material}
                domain={{ y: [0.5, 5.5] }}
                >
                    <VictoryGroup horizontal
                    offset={10}
                    style={{ data: { width: 6 } }}
                    colorScale={["brown", "tomato", "gold"]}
                    >
                    <VictoryBar
                        data={[
                        { x: 1, y: 1 },
                        { x: 2, y: 2 },
                        { x: 3, y: 3 },
                        { x: 4, y: 2 },
                        { x: 5, y: 1 }
                        ]}
                    />
                    <VictoryBar
                        data={[
                        { x: 1, y: 2 },
                        { x: 2, y: 3 },
                        { x: 3, y: 4 },
                        { x: 4, y: 5 },
                        { x: 5, y: 5 }
                        ]}
                    />
                    <VictoryBar
                        data={[
                        { x: 1, y: 1 },
                        { x: 2, y: 2 },
                        { x: 3, y: 3 },
                        { x: 4, y: 4 },
                        { x: 5, y: 4 }
                        ]}
                    />
                </VictoryGroup>
                </VictoryChart>
            </div>

            <div style={{marginLeft: "30%", marginRight: "30%"}}>
                <VictoryPie
                    style={{ labels: { fill: "white" } }}
                    innerRadius={100}
                    labelRadius={120}
                    labels={({ datum }) => `# ${datum.y}`}
                    labelComponent={<CustomLabel />}
                    data={[
                        { x: 1, y: 5 },
                        { x: 2, y: 4 },
                        { x: 3, y: 2 },
                        { x: 4, y: 3 },
                        { x: 5, y: 1 }
                    ]}
                />
            </div>

            <div style={{marginLeft: "30%", marginRight: "30%"}}>
            <VictoryBar polar

                data={[
                    { x: 100, y: 3 },
                    { x: 6, y: 5 },
                    { x: 50, y: 2 },

                ]}
                width={400} height={400}
                domain={{ x: [0, 360], y: [0, 5] }}
                style={{ data: { fill: "#c43a31", stroke: "black", strokeWidth: 2 } }}
            />
            </div>

            <div style={{marginLeft: "30%", marginRight: "30%"}}>
                <VictoryChart polar
                    maxDomain={{ x: 360 }}
                    height={250} width={250}
                    padding={30}
                >
                <VictoryPolarAxis dependentAxis
                    style={{
                    axis: {stroke: "none"},
                    tickLabels: { fill: "none"},
                    grid: { stroke: "grey", strokeDasharray: "4, 8" }
                    }}
                />
                <VictoryPolarAxis
                    tickValues={[0, 45, 90, 135, 180, 225, 270, 315]}
                />
                <VictoryStack
                    colorScale={["#ad1b11", "#c43a31", "#dc7a6b", "pink", "green", "blue"]}
                    style={{ data: { width: 50} }}
                >
                    <VictoryBar data={sampleDataPolar}/>
                    <VictoryBar data={sampleDataPolar2}/>
                    <VictoryBar data={sampleDataPolar3}/>
                </VictoryStack>
                </VictoryChart>
            </div>



            </>
        );
    }
}
