import React from 'react';
import {
     VictoryChart,
     VictoryAxis, 
     VictoryBar, 
     VictoryTheme, 
     VictoryVoronoiContainer,
     VictoryLabel,
     VictoryArea,
     VictoryLine,
     VictoryLegend,
     VictoryStack,
     VictoryZoomContainer
    } from 'victory';

const sharedAxisStyles = {
    tickLabels: {
      fontSize: 10
    },
    axisLabel: {
      padding: 45,
      fontSize: 10,
      fontStyle: "italic"
    }
  };

function configData(json) {
    const data = [];
    let endIdx = json.length-1;
    for (let i = endIdx; i >= 1; i--){
        let idx = endIdx-i;
        let obj = json[idx];
        let posIncr = (i===0 ? 0 : (obj.positive - json[idx+1].positive));
        let recovIncr = (i===0 ? 0 : (obj.recovered - json[idx+1].recovered));
        let deathIncr = (i===0 ? 0 : (obj.death - json[idx+1].death));
        data.push({
            index: i,
            date: obj.date,
            positive: obj.positive, 
            posIncr: posIncr,
            recovered: obj.recovered,
            recovIncr: recovIncr,
            death: obj.death,
            deathIncr: deathIncr
        });
    }
    return data;
}

function lineChartData(json, metric) {
    let array = configData(json);
    let lineData = array.map(obj => ({x: obj.index, y: obj[metric]}));
    return lineData;
}

function date(index, json){
    let date = json[index].date.toString();
    date = date.substr(4,2) + "/" + date.substr(6) + "/" + date.substr(0,4);
    return date;
}
export default class FetchData extends React.Component {
    state = {
        loading: true,
        json: {}
        //NOTE: json objects ordered from most recent date to oldest date
    };

    async componentDidMount() {
        const url = "https://covidtracking.com/api/us/daily";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({loading: false, json: data});
    }


    render() {
        if (this.state.loading){
            return(<div>loading...</div>);
        }

        return (
           <> 
           {/* Bar Chart: Change in Positive Cases of COVID-19 USA */}
            <VictoryChart
            height={400}
            width={400}
            padding={100}
            theme={VictoryTheme.material}
            animate={{
                duration: 500
            }}
            containerComponent={
                <VictoryVoronoiContainer
                  labels={({ datum }) => `${date(datum.index, this.state.json)}: ${datum.posIncr} new case(s)`}
                />
              }
            >
                 <VictoryLabel
                    fontSize={10}
                    x={200}
                    y={60}
                    textAnchor="middle"
                    text="COVID-19 USA: Daily Change in Positive Cases"
                />
                <VictoryAxis
                    style={sharedAxisStyles}
                    tickValues={[0, this.state.json.length]}
                    tickFormat={[date(this.state.json.length-1, this.state.json), date(0, this.state.json)]}
                ></VictoryAxis>
                <VictoryAxis
                    dependentAxis
                    tickFormat={(x) => (`${x / 1000}k`)}
                    label="Number of New Cases"
                    style={sharedAxisStyles}
                ></VictoryAxis>

                <VictoryBar
                    data={configData(this.state.json)}
                    x="index"
                    y="posIncr"          
                />
            </VictoryChart>

            <VictoryChart
                height={400}
                width={400}
                padding={100}
                theme={VictoryTheme.material}
                animate={{
                    duration: 500
                }}
                // containerComponent={
                //     <VictoryZoomContainer zoomDomain={{x: [5, 35], y: [0, 100]}}/>
                // }
            >
                <VictoryLegend x={125} y={50}
                    orientation="horizontal"
                    y={350}
                    x={50}
                    gutter={10}
                    style={{ border: { stroke: "black" }, title: {fontSize: 10 } }}
                    data={[
                    { name: "Positive Cases", symbol: { fill: "red", type: "star" } },
                    { name: "Recoveries", symbol: { fill: "blue", type: "square" } },
                    { name: "Deaths", symbol: { fill: "black", type: "square" } }
                    ]}
                />
                 <VictoryLabel
                    fontSize={10}
                    x={200}
                    y={60}
                    textAnchor="middle"
                    text="USA: Daily Change in Positive Cases, Recoveries, Deaths"
                />
                <VictoryAxis
                    style={sharedAxisStyles}
                    tickValues={[0, this.state.json.length]}
                    tickFormat={[date(this.state.json.length-1, this.state.json), date(0, this.state.json)]}
                ></VictoryAxis>
                <VictoryAxis
                    dependentAxis
                    tickFormat={(x) => (`${x / 1000}k`)}
                    label="Number of People"
                    style={sharedAxisStyles}
                ></VictoryAxis>
                <VictoryLine
                interpolation="natural"
                style={{ data: { stroke: "#c43a31" } }}
                data={lineChartData(this.state.json, 'posIncr')}
                />
                <VictoryStack>

                    <VictoryArea
                    interpolation="natural"
                    style={{ data: { fill: "black" } }}
                    data={lineChartData(this.state.json, 'deathIncr')}
                    />
                    <VictoryArea
                    interpolation="natural"
                    style={{ data: { fill: "blue" } }}
                    data={lineChartData(this.state.json, 'recovIncr')}
                    />
                </VictoryStack>

            </VictoryChart>

             </>
        );
    }
}
