import React from 'react';
import * as V from 'victory';


function configData(json) {
    console.log(json);
    const data = [];
    for (let i = json.length-1; i >= 0; i--){
        data.push({index: i, positive: json[json.length-1-i].positive, date: json[json.length-1-i].date});
    }
    console.log('data' + JSON.stringify(data));
    return data;
}

function date(index, json){
    let date = json[index].date.toString();
    date = date.substr(4,2) + "/" + date.substr(6) + "/" + date.substr(0,4);
    return date;
}
export default class FetchData extends React.Component {
    state = {
        loading: true,
        json: {}
        //NOTE: json objects ordered from most recent date to oldest date
    };

    async componentDidMount() {
        const url = "https://covidtracking.com/api/us/daily";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({loading: false, json: data});

    }




    render() {
        if (this.state.loading){
            return(<div>loading...</div>);
        }

        return (

            <V.VictoryChart
            // domainPadding={50}
            padding={100}
            theme={V.VictoryTheme.material}
            animate={{}}
            >
                <V.VictoryAxis
                    // tickValues={[0, allData.length]}
                    // tickFormat={[0, allData.length]}
                    tickValues={[0, this.state.json.length]}
                    tickFormat={[date(this.state.json.length-1, this.state.json), date(0, this.state.json)]}
                    // tickValues={Array.from({length:this.state.json.length/10},(v,k)=>k+this.state.json.length/10)}
                    // tickFormat={Array.from({length:this.state.json.length/10},(v,k)=>k+this.state.json.length/10)}
                ></V.VictoryAxis>
                <V.VictoryAxis
                    dependentAxis
                    tickFormat={(x) => (`${x / 1000}k`)}
                ></V.VictoryAxis>
                <V.VictoryBar
                    // data={this.state.json}
                    // x="date"
                    // y="positive"

                    data={configData(this.state.json)}
                    x="index"
                    y="positive" 

                />
            </V.VictoryChart>

            // <div>
            //     {this.state.loading ? <div>loading...</div> : <div>{this.state.json[0].death} deaths</div>}
            // </div>
        );
    }
}
