import React from 'react';
import {VictoryChart, VictoryAxis, VictoryBar, VictoryTheme, VictoryVoronoiContainer} from 'victory';


function configData(json) {
    console.log(json);
    const data = [];
    for (let i = json.length-1; i >= 0; i--){
        data.push({
            index: i,
            positive: json[json.length-1-i].positive, 
            date: json[json.length-1-i].date
        });
    }
    console.log('data' + JSON.stringify(data));
    return data;
}

function date(index, json){
    let date = json[index].date.toString();
    date = date.substr(4,2) + "/" + date.substr(6) + "/" + date.substr(0,4);
    return date;
}
export default class FetchData extends React.Component {
    state = {
        loading: true,
        json: {}
        //NOTE: json objects ordered from most recent date to oldest date
    };

    async componentDidMount() {
        const url = "https://covidtracking.com/api/us/daily";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({loading: false, json: data});

    }




    render() {
        if (this.state.loading){
            return(<div>loading...</div>);
        }

        return (

            <VictoryChart
            padding={100}
            theme={VictoryTheme.material}
            animate={{}}
            containerComponent={
                <VictoryVoronoiContainer
                  labels={({ datum }) => `${date(datum.index, this.state.json)}, ${datum.positive}`}
                />
              }
            >
                <VictoryAxis
                    tickValues={[0, this.state.json.length]}
                    tickFormat={[date(this.state.json.length-1, this.state.json), date(0, this.state.json)]}
                ></VictoryAxis>
                <VictoryAxis
                    dependentAxis
                    tickFormat={(x) => (`${x / 1000}k`)}
                ></VictoryAxis>
                <VictoryBar
                    data={configData(this.state.json)}
                    x="index"
                    y="positive"          
                />
            </VictoryChart>

            // <div>
            //     {this.state.loading ? <div>loading...</div> : <div>{this.state.json[0].death} deaths</div>}
            // </div>
        );
    }
}
