import React from 'react';
import * as V from 'victory';

function configData(json) {
    const data = [];
    for (let i = json.length-1; i >= 0; i--){
        data.push({index: i, positive: json[json.length-1-i].positive});
    }
    console.log(data);
    return data;
}
export default class FetchData extends React.Component {
    state = {
        loading: true,
        json: {}
    };

    async componentDidMount() {
        const url = "https://covidtracking.com/api/us/daily";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({loading: false, json: data});
        console.log(this.state.json[0]);
        console.log(typeof(this.state.json));
    }




    render() {
        return (

            <V.VictoryChart
            // domainPadding={50}
            padding={100}
            theme={V.VictoryTheme.material}
            >
                <V.VictoryAxis
                    tickValues={[this.state.json[0].date, this.state.json[this.state.json.length-1].date]}
                    tickFormat={[0, this.state.json.length]}
                    // tickValues={Array.from({length:this.state.json.length/10},(v,k)=>k+this.state.json.length/10)}
                    // tickFormat={Array.from({length:this.state.json.length/10},(v,k)=>k+this.state.json.length/10)}
                ></V.VictoryAxis>
                <V.VictoryAxis
                    dependentAxis
                    tickFormat={(x) => (`${x / 1000}k`)}
                ></V.VictoryAxis>
                <V.VictoryBar
                    // data={this.state.json}
                    // x="date"
                    // y="positive"

                    data={configData(this.state.json)}
                    x="index"
                    y="positive" 

                />
            </V.VictoryChart>

            // <div>
            //     {this.state.loading ? <div>loading...</div> : <div>{this.state.json[0].death} deaths</div>}
            // </div>
        );
    }
}
